
public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Einen Zähler von 0 bis 1000 erstellen
		for (int i = 0; i < 1000; i++) {
			System.out.println(i);
		}
		// gib eine Leere zeile aus!
		System.out.println();

		// Einen Zähler von 100 bis 10000 erstellen
		for (int i = 100; i < 10000; i++) {
			System.out.println(i);
		}
		// gib eine Leere zeile aus!
		System.out.println();

		// Einen Zähler von 27 bis 18256 in 3erschritten erstellen
		for (int i = 27; i <= 18256; i = i + 3) {
			System.out.println(i);
		}
		// gib eine Leere zeile aus!
		System.out.println();

	}

}
